sys_script_fix.script:
var grMetricType = new GlideAggregate('sa_source_metric_type');
grMetricType.addEncodedQuery('sourceSTARTSWITHOI_UIM');
grMetricType.addAggregate('COUNT');
grMetricType.query();
if (grMetricType.next());
var metricCount = grMetricType.getAggregate('COUNT');

//gs.info("Metric count is " +  metricCount);

var COUNT = 0;
var grNode = new GlideAggregate('em_event');
grNode.addEncodedQuery('sourceSTARTSWITHoi_uim');
grNode.addAggregate('COUNT');
grNode.groupBy('node');
grNode.query();
while (grNode.next()) {
    var nodeCount = grNode.getAggregate('COUNT');

   // gs.print("Metric count for CI " + grNode.node +" is "+nodeCount);
    COUNT++;
}
//gs.print("The unique node count is " + COUNT);

//gs.print("Total count is  " + metricCount * COUNT);

var grOI = new GlideRecord('x_20467_scalingmid_oi_mid_server_scaling');
grOI.initialize();
grOI.report_ran = new GlideDateTime().getDisplayValue();
grOI.number_of_metrics_monitored = metricCount;
grOI.number_of_nodes_monitored = COUNT;
var totalCount = metricCount*COUNT;
grOI.total_number_metrics_by_nodes = totalCount;
if(totalCount > 10000)
grOI.recommendation = 'two';
else
	grOI.recommendation = 'one';
grOI.insert();
